// Helper styles for demo
import "./helper.css";
import { MoreResources, DisplayFormikState } from "./helper";

import React from "react";
import { Formik } from "formik";
import * as Yup from "yup";

const regEx = /^[a-zA-Z ]*$/;

class AddRecipe extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            text: ''
        }
    }

    submitForm = e => {
        e.preventDefault();

        fetch('https://foodwaste-ecb78.firebaseio.com/recipes', 
        {method: 'POST',
          body: JSON.stringify()})

    }

    render(){
        return(
            <div className="HeaderAdd">
    <h1>
      Nowy przepis
    </h1>

    <Formik
      initialValues={{ name: "", category: "", description: "", products: "", cookingTime: "", weight: "", imageUrl: "", portions: "" }}
      onSubmit={async values => {
        await new Promise(resolve => setTimeout(resolve, 500));
        alert(JSON.stringify(values, null, 2));
      }}
      validationSchema={Yup.object().shape({
        name: Yup.string()
          .required("Required")
          .matches(regEx, 'Możesz użyć tylko słów'),
        category: Yup.string(),
        description: Yup.string()
          .required("Required"),
        products: Yup.string()
        .required("Required"),
        cookingTime: Yup.number()
         .integer()
         .positive(),
        weight: Yup.number()
            .required("Required"),
        imageUrl: Yup.string()
            .url(),
        portions: Yup.number()
            .integer()
            .positive()

      })}
    >
      {props => {
        const {
          values,
          touched,
          errors,
          dirty,
          isSubmitting,
          handleChange,
          handleBlur,
          handleSubmit,
          handleReset
        } = props;
        return (
          <form onSubmit={handleSubmit}>
            <label htmlFor="name" style={{ display: "block" }}>
              Nazwa przepisu
            </label>
            <input
              id="name"
              placeholder="Wpisz nazwę przepisu"
              type="text"
              value={values.name}
              onChange={handleChange}
              onBlur={handleBlur}
              className={
                errors.name && touched.name
                  ? "text-input error"
                  : "text-input"
              }
            />
            {errors.name && touched.name && (
              <div className="input-feedback">{errors.name}</div>
            )}


            <label htmlFor="category" style={{ display: "block" }}>
              Rodzaj kuchni
            </label>
            <input
              id="category"
              placeholder="Wybierz kuchnię"
              type="text"
              value={values.category}
              onChange={handleChange}
              onBlur={handleBlur}
              className={
                errors.category && touched.category
                  ? "text-input error"
                  : "text-input"
              }
            />
            {errors.category && touched.category && (
              <div className="input-feedback">{errors.name}</div>
            )}


            <label htmlFor="description" style={{ display: "block" }}>
              Opis
            </label>
            <input
              id="description"
              placeholder="Opisz swój przepis"
              type="text"
              value={values.description}
              onChange={handleChange}
              onBlur={handleBlur}
              className={
                errors.description && touched.description
                  ? "text-input error"
                  : "text-input"
              }
            />
            {errors.description && touched.description && (
              <div className="input-feedback">{errors.description}</div>
            )}



            <label htmlFor="category" style={{ display: "block" }}>
              Rodzaj kuchni
            </label>
            <input
              id="category"
              placeholder="Wybierz kuchnię"
              type="text"
              value={values.category}
              onChange={handleChange}
              onBlur={handleBlur}
              className={
                errors.category && touched.category
                  ? "text-input error"
                  : "text-input"
              }
            />
            {errors.category && touched.category && (
              <div className="input-feedback">{errors.name}</div>
            )}


            <button
              type="button"
              className="outline"
              onClick={handleReset}
              disabled={!dirty || isSubmitting}
            >
              Reset
            </button>
            <button type="submit" disabled={isSubmitting}>
              Submit
            </button>


          </form>
        );
      }}
    </Formik>


  </div>



        )




    }



}

export default AddRecipe;
