class Search extends React.Component {
	state = {
		products: '',
		recipes: []
	};

	componentDidMount() {
		fetch('https://foodwaste-ecb78.firebaseio.com/recipes.json').then((respond) => respond.json()).then((object) =>
			Object.keys(object).map((key) => {
				object[key].id = key;
				return object[key];
