import React from "react";
import styles from "./MainContent.module.css";
import AddRecipe from "./AddRecipe";
import {RecipesFromBase} from "./RecipesFromBase"
import {Icon, Button} from 'semantic-ui-react';

class MainContent extends React.Component {
    constructor(props){
        super(props);
        this.state = { modal: false

        }
    }

    render() {
        return (
            <div className={styles.MainContent}>
            <RecipesFromBase/>
            <Button size="massive" circular icon="plus" onClick={ () =>  { this.setState({ modal: this.state.modal})}} style={{position: "fixed", bottom: 50, right: 50, background: "yellow" }} ></Button>
            { this.state.modal &&  <AddRecipe/>}
            </div>
        )
    }
}

export default MainContent;
