import React from "react";
import { Input } from "semantic-ui-react";
import styles from './AddToForm.module.css'




function create(id) {
    fetch(`https://new-project-cwiczenia1.firebaseio.com/${id}.json`, {
      method: 'POST',
      body: JSON.stringify({
        name: this.state.form.name
      })
    })
    .then(res => res.json())
    .then(res => console.log(res));
  }


class AddForm extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            form: {
            name: "",
            category: "",
            description: "",
            products: "",
            cookingTime: "",
            weight: "",
            portions: ""
        }
    }
} 

 changeHandler = event => {
        const name = event.target.name;
        const value = event.target.value;

        this.setState({
          form: {
            ...this.state.form,
            [name]: value
          }
        });
      };

   render() {
       return(
        <form className={styles.myForm}>
        <Input
          onChange={this.changeHandler}
          value={this.state.form.name}
          type="text"
          name="name"
          placeholder="Nazwa potrawy"
        />
        <Input
          onChange={this.changeHandler}
          value={this.state.form.category}
          type="text"
          name="category"
          placeholder="Rodzaj kuchni"
        />
        <Input
          onChange={this.changeHandler}
          value={this.state.form.description}
          type="text"
          name="description"
          placeholder="Krótki opis przepisu"
        />
        <Input
          onChange={this.changeHandler}
          value={this.state.form.products}
          type="text"
          name="products"
          placeholder="Składniki"
        />

         <Input
          onChange={this.changeHandler}
          value={this.state.form.cookingTime}
          type="text"
          name="cookingTime"
          placeholder='Czas przygotowania...'
        />
        <Input
        onChange={this.changeHandler}
        label={{ basic: true, content: 'kg' }}
        labelPosition='right'
        placeholder='wpisz wagę...'
        name="weight"
        value={this.state.form.weight}
        />
    <Input
          onChange={this.changeHandler}
          value={this.state.form.portions}
          type="number"
          name="portions"
          placeholder='Ilość porcji ...'
        />
        <button type="submit" onClick={create}>Add Receipe</button>

      </form>

);
}
}

export default AddForm ;
