import React, { Component } from 'react';
import UsersList from './UserList';
import RecipiesToRender from '../RecipesToRender';
import Recipes from '../Recipes';

class Search extends Component {
	constructor() {
		super();

		this.state = {
			filteredProducts: Recipes
		};
	}
	render() {
		return (
			<div>
				<input type="search" />
				<ul>
					<li>Kurczak</li>
					<li>Wo≈Çowina</li>
					<li>Kasia</li>
					<li>Tomek</li>
				</ul>
				<input onInput={this.filterUsers.bind(this)} />
				<UsersList users={this.state.filteredProducts.map((r) => r.name)} />
			</div>
		);
	}

	filterUsers(e) {
		const text = e.currentTarget.value;
		const filteredUsers = this.getFilteredProductsForText(text);
		this.setState({
			filteredUsers
		});
	}

	getFilteredProductsForText(text) {
		return Recipes.filter(({ products }) => products.toLowerCase().includes(text.toLowerCase()));
	}
}
export default Search;
