import React from "react";
import { Input } from "semantic-ui-react";
import styles from './AddToForm.module.css'






class AddForm extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            form: {
            name: "",
            category: "",
            description: "",
            products: "",
            cookingTime: "",
            weight: "",
            portions: "",
            form: []
        }
    }
} 

 changeHandler = event => {
        const name = event.target.name;
        const value = event.target.value;

        this.setState({
          form: {
            ...this.state.form,
            [name]: value

          }
        });
      };

      componentDidMount() {
        fetch(`https://myfirstproject-b5855.firebaseio.com/.json`)
            .then(res => res.json())
            .then(obj => Object.values(obj))
            .then(form => this.setState({form: form}));
    }


      create() {
        fetch(`https://myfirstproject-b5855.firebaseio.com/.json`, {
          method: 'POST',
          body: JSON.stringify({
            form: this.state.form
          })
        })
        .then(res => res.json())
        .then(res => console.log(res));
      }     

    //   get(recipe) {
    //     fetch(`https://myfirstproject-b5855.firebaseio.com/${recipe}.json`, {
    //       method: 'GET'
    //     })
    //     .then(res => res.json())
    //     .then(res => console.log(res));
    //   }

   render() {
       return(

        <form onSubmit={e => {
            e.preventDefault();
            this.create();
        }} className={styles.myForm}>
        <Input
          onChange={this.changeHandler}
          value={this.state.form.name}
          type="text"
          name="name"
          placeholder="Nazwa potrawy"
        />
        <Input
          onChange={this.changeHandler}
          value={this.state.form.category}
          type="text"
          name="category"
          placeholder="Rodzaj kuchni"
        />
        <Input
          onChange={this.changeHandler}
          value={this.state.form.description}
          type="text"
          name="description"
          placeholder="Krótki opis przepisu"
        />
        <Input
          onChange={this.changeHandler}
          value={this.state.form.products}
          type="text"
          name="products"
          placeholder="Składniki"
        />

         <Input
          onChange={this.changeHandler}
          value={this.state.form.cookingTime}
          type="text"
          name="cookingTime"
          placeholder='Czas przygotowania...'
        />
        <Input
        onChange={this.changeHandler}
        label={{ basic: true, content: 'kg' }}
        labelPosition='right'
        placeholder='wpisz wagę...'
        name="weight"
        value={this.state.form.weight}
        />
    <Input
          onChange={this.changeHandler}
          value={this.state.form.portions}
          type="number"
          name="portions"
          placeholder='Ilość porcji ...'
        />
        <button type="submit">Add Receipe</button>
        {/* {this.state.form.map(form => <p>{form.text}</p>)} */}
      </form>




       )
    }
}

export default AddForm ;
