import React from 'react';

// import RecipesToRender from '../RecipesToRender';
import ProductList from './ProductsList';
import styles from './Search.module.css';

// Search input by products
class Search extends React.Component {
	state = {
		products: ''
	};

	componentDidMount() {
		fetch('https://foodwaste-ecb78.firebaseio.com/recipes.json')
			.then((res) => res.json())
			.then((obj) => Object.values(obj))
			.then((allRecipes) => this.setState({ recipes: allRecipes }));
	}

	handleInputChange = () => {
		this.setState({
			products: this.search.value
		});
	};

	constructor() {
		super();

		this.state = {
			filteredProducts: 'this.Recipies.products'
		};
	}

	render() {
		return (
			<form>
				<input
					className={styles.searchElement}
					type="search"
					placeholder="Search for product..."
					ref={(input) => (this.search = input)}
					onChange={this.handleInputChange}
				/>
			</form>
		);
	}
}

export default Search;
