import React from 'react';

import RecipiesToRender from '../RecipesToRender';
import ProductList from './ProductsList';

// Search input by products
class Search extends React.Component {
	state = {
		products: ''
	};

	handleInputChange = () => {
		this.setState({
			products: this.search.value
		});
	};

	constructor() {
		super();

		this.state = {
			filteredProducts: RecipiesToRender
		};
	}

	render() {
		return (
			<form>
				<input
					type="search"
					placeholder="Search for product..."
					ref={(input) => (this.search = input)}
					onChange={this.handleInputChange}
					onInput={this.filteredProducts.bind(this)}
				/>
				<ProductList product={this.state.filteredProducts} />
				<p>{this.state.products}</p>
			</form>
		);
	}
}

